import React from 'react';
import UserGrid from '../Profile/UserGrid';
import { Link, useLocation, useParams } from 'react-router-dom';
import styled, {css} from 'styled-components';
import Posts from '../Posts';
import { Image } from '../App';


const PhotoGrid = styled.div `
  display: grid;
  grid-template-columns: repeat(3, 305px);
  justify-content: center;
  gap: 20px;
  ${({cascade}) => cascade && css `
    grid-auto-rows: 200px;
  `}
`
const LinkGrid = styled.div`
  display: grid;
  grid-template-columns: auto auto;
  justify-content: center;
  gap: 30px;
  margin-bottom: 20px;
`

const TabLink = styled(Link)`
  text-decoration: none;
  color: #ccc;
  text-transform: uppercase;
  letter-spacing: 3px;
  ${({selected}) => selected && 'color: black;'}
`



export function Gallery({match, useParams}) {
  let location = useLocation();
  let { id } = useParams();
  const cascade = location.search === '?type=cascade';
  return (
    <div>
      <UserGrid />
      <LinkGrid>
        <TabLink selected={!cascade} to={`${match.useParams.id}`} >
          square
        </TabLink>
        <TabLink selected={cascade} to={{pathname:`${match.useParams}`}}>
          cascade
        </TabLink>
      </LinkGrid>
      <PhotoGrid cascade={cascade}>
        {Posts.map(i => (
          <Link
            key={i.id}
            to={{
              pathname: `/img/${i.id}`,
              // This is the trick! This link sets
              // the `background` in location state.
              state: { background: location }
            }}
          >
            <Image index={i.id} />
          </Link>
        ))}
      </PhotoGrid>
    </div>
  );
}
